import React, { useState } from "react";
import "./App.css";
import CustomButton from "./components/customButton";
import Todo from "./components/Todo";

const App = () => {
  const [todoList, setTodoList] = useState([
    {
      id: 1,
      title: "Java Script Í≥µÎ∂ÄÌïòÍ∏∞",
      body: "ÌòºÍ≥µÏä§ chapter7",
      isDone: false,
    },
    {
      id: 2,
      title: "Ïö¥ÎèôÌïòÍ∏∞",
      body: "9ÏãúÎ∂ÄÌÑ∞ 10ÏãúÍπåÏßÄ Îπ°ÏãúÍ≤å Ïö¥Îèô",
      isDone: false,
    },
    {
      id: 3,
      title: "ÏÑ∏ÌÉÅÍ∏∞ÎèåÎ¶¨Í∏∞",
      body: "ÏàòÍ±¥Îπ®Îûò",
      isDone: false,
    },
  ]);

  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");

  const addListHandler = () => {
    const newList = {
      id: todoList.length + 1,
      title: title,
      body: body,
    };
    setTodoList([...todoList, newList]);
  };
  const deleteListHandler = (id) => {
    const newTodoList = todoList.filter((todo) => todo.id !== id);
    setTodoList(newTodoList);
  };

  const completeListHandler = (id) => {
    const completedTodoList = todoList.map((todo) => {
      if (todo.id === id) {
        return {
          ...todo,
          isDone: !todo.isDone,
        };
      } else {
        return { ...todo };
      }
    });
    setTodoList(completedTodoList);
  };
  return (
    <div>
      <div className="title-style">
        My To do List <p className="react">ü§çReactü§ç</p>
      </div>
      <div className="header-style">
        Ï†úÎ™©
        <input
          className="jemok"
          value={title}
          placeholder="Ï†úÎ™©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî"
          // Ïù∏Ìíã Ïù¥Î≤§Ìä∏Î°ú Îì§Ïñ¥Ïò® ÏûÖÎ†• Í∞íÏùÑ nameÏùò Í∞íÏúºÎ°ú ÏóÖÎç∞Ïù¥Ìä∏
          onChange={(e) => setTitle(e.target.value)}
        />
        ÎÇ¥Ïö©
        <input
          value={body}
          placeholder="ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî"
          // Ïù∏Ìíã Ïù¥Î≤§Ìä∏Î°ú Îì§Ïñ¥Ïò® ÏûÖÎ†• Í∞íÏùÑ ageÏùò Í∞íÏúºÎ°ú ÏóÖÎç∞Ïù¥Ìä∏
          onChange={(e) => setBody(e.target.value)}
        />
        <CustomButton color="rgb(93, 39, 0)" onClick={addListHandler}>
          Ï∂îÍ∞ÄÌïòÍ∏∞
        </CustomButton>
      </div>
      <p className="jinhaeng">ÏßÑÌñâÏ§ëüî®üî®üî®</p>
      <div className="app-style">
        {todoList.map((todo) => {
          return <Todo handleDelete={deleteListHandler} todo={todo}></Todo>;
        })}
      </div>

      <p className="wanryo">üéä ÏôÑÎ£å üéä</p>
      <div className="app-style">
        {todoList.map((todo) => {
          return <Todo handleComplete={completeListHandler} todo={todo}></Todo>;
        })}
      </div>
    </div>
  );
};

export default App;
