import logo from "./logo.svg";
import "./App.css";
import { useState } from "react";

function App() {
  let [list, setList] = useState([{ id: 1, title: "react를 배워봅시다" }]);
  let addListHandler = () => {
    let newList = {
      id: list.length + 1,
      title: title,
    };
    setList([...list, newList]);
  };
  let [title, setTitle] = useState("");

  return (
    <>
      <div className="insertplace">
        <input
          value={title}
          placeholder="무엇을 할까요?"
          onChange={(e) => setTitle(e.target.value)}
        />
        <button onClick={addListHandler}>추가하기</button>
      </div>
      <h1 className="App">Todo List</h1>;{/* <Card /> */}
      ); };
      <div className="cards">
        {list.map((title) => {
          return (
            <div>
              list={list} key={list.id}
            </div>
          );
        })}
      </div>
      ;
    </>
  );
}
export default App;
