import React from "react";
import "./App.css"; // üî• Î∞òÎìúÏãú App.css ÌååÏùºÏùÑ import Ìï¥Ï§òÏïº Ìï©ÎãàÎã§.

//  User Ïª¥Ìè¨ÎÑåÌä∏Î•º Î∂ÑÎ¶¨Ìï¥ÏÑú Íµ¨ÌòÑ

function CustomButton(props) {
  return <button onClick={props.onClick}>{props.children}</button>;
}

function User(props) {
  return (
    <div className="user-card">
      <div>{props.user.age}ÏÇ¥ - </div>
      <div>{props.user.name}</div>
      <CustomButton onClick={() => props.handleDelete(props.user.id)}>
        ÏÇ≠Ï†úÌïòÍ∏∞
      </CustomButton>
    </div>
  );
}

const App = () => {
  const [users, setUsers] = useState([
    { id: 1, age: 30, name: "ÏÜ°Ï§ëÍ∏∞" },
    { id: 2, age: 24, name: "ÏÜ°Í∞ï" },
    { id: 3, age: 21, name: "ÍπÄÏú†Ï†ï" },
    { id: 4, age: 29, name: "Íµ¨ÍµêÌôò" },
  ]);
  const [name, setName] = useState(""); // <-- Ïú†Ï†ÄÏùò ÏûÖÎ†•Í∞íÏùÑ Îã¥ÏùÑ ÏÉÅÌÉú
  const addUserHandler = () => {
    const newUser = {
      id: users.length + 1,
      age: 30,
      name: name,
    };

    setUsers([...users, newUser]);
  };
  const deleteUserHandler = (id) => {
    const newUserList = users.filter((user) => user.id !== id);
    setUsers(newUserList);
  };
  return (
    <div className="app-container">
      <input
        placeholder="Ïù¥Î¶ÑÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî"
        value={name}
        // Ïù∏Ìíã Ïù¥Î≤§Ìä∏Î°ú Îì§Ïñ¥Ïò® ÏûÖÎ†• Í∞íÏùÑ nameÏùò Í∞íÏúºÎ°ú ÏóÖÎç∞Ïù¥Ìä∏
        onChange={(e) => setName(e.target.value)}
      />
      {users.map((user) => {
        return (
          <User user={user} key={user.id} handleDelete={deleteUserHandler} />
        );
      })}
      <CustomButton onClick={addUserHandler}>Ï∂îÍ∞ÄÌïòÍ∏∞</CustomButton>
    </div>
  );
};

export default App;
