          onChange={(e) => setBody(e.target.value)}
        />
        <CustomButton color="rgb(93, 39, 0)" onClick={addListHandler}>
          Ï∂îÍ∞ÄÌïòÍ∏∞
        </CustomButton>
      </div>
      <p className="jinhaeng">ÏßÑÌñâÏ§ë</p>
      <div className="app-style">
        {todoList.map((todo) => {
          return <Todo handleDelete={deleteListHandler} todo={todo}></Todo>;
        })}
      </div>
      <p className="wanryo">ÏôÑÎ£å!!!!!</p>
    </div>
  );
};

