import React from "react";
import "./App.css"; // üî• Î∞òÎìúÏãú App.css ÌååÏùºÏùÑ import Ìï¥Ï§òÏïº Ìï©ÎãàÎã§.
import { useState } from "react";

function User(props) {
  return (
    <div className="user-card">
      <div>{props.user.name}</div>
    </div>
  );
}

const App = () => {
  const [users, setUsers] = useState([{ id: 1, name: "reactÍ≥µÎ∂ÄÌïòÍ∏∞" }]);
  const [name, setName] = useState("");
  const addUserHandler = () => {
    const newUser = {
      id: users.length + 1,
      name: name,
    };
    setUsers([...users, newUser]);
  };
  return (
    <div className="app-container">
      <input
        value={name}
        // Ïù∏Ìíã Ïù¥Î≤§Ìä∏Î°ú Îì§Ïñ¥Ïò® ÏûÖÎ†• Í∞íÏùÑ nameÏùò Í∞íÏúºÎ°ú ÏóÖÎç∞Ïù¥Ìä∏
        onChange={(e) => setName(e.target.value)}
      />
      {users.map((user) => {
        return <User user={user} key={user.id} />;
      })}
      <button onClick={addUserHandler}>Ï∂îÍ∞ÄÌïòÍ∏∞</button>
    </div>
  );
};

export default App;
