import React, { useState } from "react";
// import MyTodoList from "./component/MyTodoList";
import "./App.css";

function CustomButton(props) {
  const { color, onClick, children } = props;
  if (color) {
    return (
      <button
        style={{ backgroundColor: color, color: "white", borderRadius: "15px" }}
        onClick={onClick}
      >
        {children}
      </button>
    );
  }

  return <button onClick={onClick}>{children}</button>;
}

function Todo(props) {
  return (
    <div className="square-style">
      {props.todo.title}- {props.todo.body}
      <CustomButton
        color="red"
        onClick={() => props.handleDelete(props.todo.id)}
      >
        delete
      </CustomButton>
    </div>
  );
}
const App = () => {
  const [todoList, setTodoList] = useState([
    {
      id: 1,
      title: "Java Script 공부하기",
      body: "chapter7 공부하기",
      isDone: false,
    },
    {
      id: 2,
      title: "운동하기",
      body: "9시부터 10시까지 빡시게 운동",
      isDone: false,
    },
    {
      id: 3,
      title: "세탁기돌리기",
      body: "수건빨래",
      isDone: false,
    },
  ]);
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");

  const addListHandler = () => {
    const newList = {
      id: todoList.length + 1,
      title: title,
      body: body,
    };
    setTodoList([...todoList, newList]);
  };
  const deleteListHandler = (id) => {
    const newTodoList = todoList.filter((todo) => todo.id !== id);
    setTodoList(newTodoList);
  };
  return (
    <div>
      <div className="app-style">
        {todoList.map((todo) => {
          return <Todo handleDelete={deleteListHandler} todo={todo}></Todo>;
        })}
      </div>
      <input
        value={title}
        placeholder="제목을 입력해주세요"
        // 인풋 이벤트로 들어온 입력 값을 name의 값으로 업데이트
        onChange={(e) => setTitle(e.target.value)}
      />
      <input
        value={body}
        placeholder="내용을 입력해주세요"
        // 인풋 이벤트로 들어온 입력 값을 age의 값으로 업데이트
        onChange={(e) => setBody(e.target.value)}
      />
      <CustomButton color="green" onClick={addListHandler}>
        추가하기
      </CustomButton>
    </div>
  );
};

